import React, { useState } from "react";
import useLocalStorage from "../hooks/useLocalStorage";
import axios from "axios";
import config from "../config";


function addInput() {
  var container = document.getElementById("input-container");
  var inputGroup = document.createElement("div");
  inputGroup.classList.add("input-group");
  inputGroup.innerHTML = '<input type="text" name="dynamicInput[]" required> <button type="button" onclick="removeInputGroup(this)">Remove</button>';
  container.appendChild(inputGroup);
}

function removeInput() {
  var container = document.getElementById("input-container");
  if (container.lastElementChild !== null) {
      container.removeChild(container.lastElementChild);
  }
}

function removeInputGroup(button) {
  button.parentNode.remove();
}



const SubmitEventForm = (user) => {
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    location: "",
    seat: "",
    startDate: "",
    endDate: "",
    openTicket: "",
    closeTicket: "",
    totalTicket: "",
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setFormData({
      ...formData,
      userID: user.username,
    });
    const form = new FormData();
    form.append("name", formData.name);
    form.append("description", formData.description);
    form.append("location", formData.location);
    form.append("seat", formData.seat);
    form.append("startDate", formData.startDate);
    form.append("endDate", formData.endDate);
    form.append("openTicket", formData.shippingCosts);
    form.append("closeTicket", formData.expiration);
    form.append("totalTicket", formData.startDate);
    form.append("file", formData.photo);
    form.append("userID", user.id);
    const response = await axios({
      method: "post",
      url: `${config.itemServiceUrl}/submit-listing`,
      data: form,
      headers: {
        "Content-Type": "multipart/form-data",
      },
    });
    if (response.status === 200) {
      alert("Listing submitted successfully");
      setFormData({
        name: "",
      description: "",
      location: "",
      seat: "",
      startDate: "",
      endDate: "",
      openTicket: "",
      closeTicket: "",
      totalTicket: "",
      });
    }
  };

  return (
    <div className="container pt-5">
      <div className="row mt-2">
        <div className="col">
          <h1>Add Event</h1>
        </div>
      </div>
      <div className="row mt-2">
        <h4>
          Fields marked with a red asterisk (
          <span style={{ color: "red" }}>*</span>) are required.
        </h4>
      </div>
      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <div className="row mt-2">
          <div className="col-4">
            <label htmlFor="name" className="form-label">
              Event Name:<strong style={{ color: "red" }}>*</strong>
            </label>
            <input
              type="text"
              className="form-control"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleChange}
            />
          </div>
        </div>
        <div className="row mt-2">
          <div className="col-4">
            <label htmlFor="description" className="form-label">
              Event description:
              <strong style={{ color: "red" }}>*</strong>
            </label>
            <input
              type="text"
              className="form-control"
              id="description"
              name="description"
              value={formData.description}
              onChange={handleChange}
            />
          </div>
        </div>
        <div className="row mt-2">
          <div className="col-4">
            <label htmlFor="description" className="form-label">
              Event location:
              <strong style={{ color: "red" }}>*</strong>
            </label>
            <input
              type="text"
              className="form-control"
              id="description"
              name="description"
              value={formData.description}
              onChange={handleChange}
            />
          </div>
        </div>
        <div className="row mt-2">
          <div className="col-4">
            <label htmlFor="startPrice" className="form-label">
              Enter a start price amount:
              <strong style={{ color: "red" }}>*</strong>
            </label>
            <input
              type="number"
              className="form-control"
              id="startPrice"
              name="startPrice"
              value={formData.startPrice}
              onChange={handleChange}
            />
          </div>
        </div>
        <div className="row mt-2">
          <div className="col-4">
            <label htmlFor="buyNowPrice" className="form-label">
              Enter a buy-now price amount:
              <strong style={{ color: "red" }}>*</strong>
            </label>
            <input
              type="number"
              className="form-control"
              id="buyNowPrice"
              name="buyNowPrice"
              value={formData.buyNowPrice}
              onChange={handleChange}
            />
          </div>
        </div>
        <div className="row mt-2">
          <div className="col-4">
            <label htmlFor="quantity" className="form-label">
              Enter the quantity:<strong style={{ color: "red" }}>*</strong>
            </label>
            <input
              type="number"
              className="form-control"
              id="quantity"
              name="quantity"
              value={formData.quantity}
              onChange={handleChange}
            />
          </div>
        </div>
        <div className="row mt-2">
          <div className="col-4">
            <label htmlFor="shippingCosts" className="form-label">
              Enter the shipping costs:
              <strong style={{ color: "red" }}>*</strong>
            </label>
            <input
              type="number"
              className="form-control"
              id="shippingCosts"
              name="shippingCosts"
              value={formData.shippingCosts}
              onChange={handleChange}
            />
          </div>
        </div>
        <div className="row mt-2">
          <div className="col-4">
            <label htmlFor="startDate" className="form-label">
              Set an start date date for your auction:
              <strong style={{ color: "red" }}>*</strong>
            </label>
            <input
              type="date"
              className="form-control"
              id="startDate"
              name="startDate"
              value={formData.startDate}
              onChange={handleChange}
            />
          </div>
        </div>
        <div className="row mt-2">
          <div className="col-4">
            <label htmlFor="expiration" className="form-label">
              Set an expiration date for your auction:
              <strong style={{ color: "red" }}>*</strong>
            </label>
            <input
              type="date"
              className="form-control"
              id="expiration"
              name="expiration"
              value={formData.expiration}
              onChange={handleChange}
            />
          </div>
        </div>

        

        <div className="row my-5">
          <div className="col-4">
            <button className="btn main-button btn-primary" type="submit">
              Submit Event
            </button>
          </div>
        </div>
      </form>
    </div>
  );
};

export default SubmitEventForm;
