import React, { useState, useEffect } from "react";
import {useNavigate} from 'react-router-dom';
import axios from "axios";
import config from "../config";


function Profile({ user }) {
  const [activeListings, setActiveListings] = useState([]);
  const [shoppingCart, setShoppingCart] = useState([]);
  const [bidHistory, setBidHistory] = useState([]);
  const [userInfo, setUserInfo] = useState([]);
  const [activeUsers, setActiveUsers] = useState([]);
  const [watchlist, setWatchlist] = useState([]);

  const [lowerPrice, setLowerPrice] = useState("");
  const [upperPrice, setUpperPrice] = useState("");
  const [keyword, setKeyword] = useState("");

  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [dateList, setDateList] = useState([]);

  const [newCategory, setNewCategory] = useState("");

  const [toLinkListingID, setToLinkListingID] = useState("");
  const [toLinkCategory, setToLinkCategory] = useState("");


  const [formData, setFormData] = useState({
    fname: '',
    lname: '',
    email: '',
    phone: '',
    username: '',
    type: '',
  });
  const navigate = useNavigate();
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
        const response = await axios.post(`${config.userServiceUrl}/profile`, {
            username: formData.username,
            password: formData.password,
            confirm_password: formData.confirmPassword,
            phone: formData.phone,
            fname: formData.fname,
            lname: formData.lname,
            email: formData.email,
            type: formData.type
        });

        if (response.data.success) {
            alert('Profile updated');
            navigate('/profile');
        }
    } catch (error) {
        alert('An error occurred during profile update');
    }
  };

  useEffect(() => {
    if (!user || !user.id) return;
    axios
      .get(`${config.userServiceUrl}/user/${user.id}/profile`)
      .then((response) => {
        if (response.data.success) {
          setUserInfo(response.data.data);
        }
      })
      .catch((error) => console.log(error));

    axios
      .get(`${config.userServiceUrl}/user/${user.id}/active-listings`)
      .then((response) => {
        setActiveListings(response.data.data);
      })
      .catch((error) => console.log(error));

    axios
      .get(`${config.userServiceUrl}/user/${user.id}/bid-history`)
      .then((response) => {
        setBidHistory(response.data.data);
      })
      .catch((error) => console.log(error));

    axios
      .get(`${config.userServiceUrl}/user/${user.id}/active-users`)
      .then((response) => {
        if (response.data.success) {
          setActiveUsers(response.data.data);
        }
      })
      .catch((error) => console.log(error));

    axios
      .post(`${config.itemServiceUrl}/get-shoppingcart`, { userID: user.id })
      .then((response) => {
        setShoppingCart(response.data.data);
      })
      .catch((error) => console.log(error));

    axios
      .post(`${config.itemServiceUrl}/get-watchlist`, { userID: user.id })
      .then((response) => {
        setWatchlist(response.data.data);
      })
      .catch((error) => console.log(error));
  }, [user, user.id]);

  if (!userInfo) {
    return <div>Loading...</div>;
  } else {
    console.log(userInfo);
  }

  return (
    <div className="container pt-5">
      <h1>{userInfo.firstName + ' ' + userInfo.lastName}'s Profile</h1>

      <form onSubmit={handleSubmit}>
         {/* Basic info of the user */}
        <div className="mt-4">
          <h4 style={{ color: "maroon" }}>Profile Details</h4>
          <div className="row">
            <div className="col-4">
              <label htmlFor='fname' className="form-label">First Name</label>
              <input type="text" className="form-control" name='fname' value={userInfo.firstName} onChange={handleChange}/>
            </div>
            <div className="col-4">
              <label htmlFor='lname' className="form-label">Last Name</label>
              <input type="text" className="form-control" name='lname' value={userInfo.lastName} onChange={handleChange}/>
            </div>
          </div>

          <div className="row">
            <div className="col-4">
              <label htmlFor='email' className="form-label">Email</label>
              <input type="email" className="form-control" name='email' value={userInfo.email} disabled/>
            </div>
            <div className="col-4">
              <label htmlFor='type' className="form-label">Account Type</label>
              <input type="text" className="form-control" name='lname' value={userInfo.lastName} onChange={handleChange}/>
            </div>
          </div>

          <div className="row mt-2">
            <div className="col-4">
              <label>Email</label>
              <input
                type="email"
                className="form-control"
                value={userInfo.email}
                disabled
              />
            </div>
          </div>

          <div className="row mt-2">
            <div className="col-4">
              <label>Username</label>
              <input
                type="text"
                className="form-control"
                value={userInfo.userName}
                disabled
              />
            </div>
          </div>

          <div className="row mt-2">
            <div className="col-4">
              <label>Date Joined</label>
              <input
                type="text"
                className="form-control"
                value={new Date(userInfo.dateJoined).toLocaleDateString()}
                disabled
              />
            </div>
          </div>

          <div className="row mt-2">
            <div className="col-4">
              <label>Account Type</label>
              <input
                type="text"
                className="form-control"
                value={userInfo.isAdmin ? "Admin" : userInfo.isGuest ? "Guest" : "Enterprise"}
                disabled
              />
          </div>

          <div className='row my-4'>
            <div className="col-4">
              <button type="submit" className="btn btn-danger">Update Profile</button>
            </div>
          </div>
        </div>

      </div>
      </form>

    </div>
  );
}

export default Profile;
