import React,  { useState } from 'react';
import { Navbar, Nav, NavDropdown, Container } from 'react-bootstrap';
import { LinkContainer } from 'react-router-bootstrap';
import {useNavigate} from "react-router-dom";

function CustomNavbar({ isLoggedIn, user, onLogout }) {
  const navigate = useNavigate();
  const [userInfo, setUserInfo] = useState(user);

  const handleLogout = () => {
    onLogout();
    navigate('/');
  };
  return (
    <Navbar bg="dark" variant="dark" expand="lg">
      <Container>
        <LinkContainer to="/">
          <Navbar.Brand>
            <img
              src={process.env.PUBLIC_URL + '/static/img/logo.png'}
              alt="Logo"
              className="logo-img"
            />
          <span className="auction-text">Ticket Booking System</span>
          </Navbar.Brand>
        </LinkContainer>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="ms-auto">
            <LinkContainer to="/">
              <Nav.Link>Auctions</Nav.Link>
            </LinkContainer>
            <LinkContainer to="/submit-listing">
              <Nav.Link>Discounts</Nav.Link>
            </LinkContainer>
            {isLoggedIn ? (
              <NavDropdown title={user.username} id="basic-nav-dropdown">
                <LinkContainer to="/profile">
                  <NavDropdown.Item>Profile</NavDropdown.Item>
                </LinkContainer>
                {userInfo.isGuest (
                  <NavDropdown.Item>Ticket History</NavDropdown.Item>
                )}
                <NavDropdown.Divider />
            <NavDropdown.Item onClick={handleLogout}>Logout</NavDropdown.Item>
              </NavDropdown>
            ) : (
              <>
                <LinkContainer to="/register">
                  <Nav.Link>Sign Up</Nav.Link>
                </LinkContainer>
                <LinkContainer to="/login">
                  <Nav.Link>Log In</Nav.Link>
                </LinkContainer>
              </>
            )}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default CustomNavbar;
